mapper.select:
SELECT * FROM ants_chat_user
       WHERE studentId =#{studentId}
           AND contactor=#{contactor}

SELECT contactor , contactorName ,contactorAvatar
       FROM ants_chat_user
           WHERE studentId = #{id}

SELECT information ,latestTime
       FROM ants_chat_user
           WHERE studentId = #{studentId}
               AND contactor = #{contactor}

mapper.update:
UPDATE ants_chat_user
    SET information =concat(information,#{information}) ,latestTime = #{latestTime}
        WHERE studentId = #{studentId}
            AND contactor = #{contactor}
