mapper.select:
		SELECT goodsId,goodsName,goodsDescribe,goodsPicture,
         	   goodsVideo,goodsClass,goodsPrice,goodsState,
         	   goodsBargin,goodsBelong,goodsWay,uploadTime
				  FROM ants_goods_base
					  WHERE goodsId != #{goodsId}
					  		AND goodsBelong = (
								SELECT goodsBelong
									FROM ants_goods_base
										WHERE goodsId = #{goodsId}
								)

SELECT COUNT(*)
		  FROM ants_goods_base
			  WHERE goodsId != #{goodsId}
			  		ADN goodsBelong = (
						SELECT goodsBelong
							FROM ants_goods_base
								WHERE goodsId = #{goodsId}
						)
