mapper.select:
SELECT subClassId,subClassName,parentClass
            FROM ants_class_sub
                WHERE parentClass = #{parentClass}

SELECT
      
          FROM ants_goods_base
              WHERE goodsClass
                IN(
                    SELECT subClassId FROM ants_class_sub
                        WHERE parentClass = #{parentId}
                  ) LIMIT #{head},#{tail}

        SELECT
              
                    FROM ants_goods_base
        
            WHERE goodsClass
                  IN (
                      SELECT subClassId
                        FROM ants_class_sub
                            WHERE parentClass = #{parentId}
                     ) LIMIT #{head},#{tail}
        

        
            WHERE goodsClass = #{childId} LIMIT #{head},#{tail}

        SELECT COUNT(*) FROM ants_goods_base
        
            WHERE goodsClass
                  IN (
                    SELECT subClassId
                        FROM ants_class_sub
                            WHERE parentClass = #{parentId}
                     ) LIMIT #{head},#{tail}
        

        
            WHERE goodsClass = #{childId} LIMIT #{head},#{tail}

        SELECT
              
                    FROM ants_goods_base
        
            WHERE goodsClass
                   IN (
                      SELECT subClassId
                          FROM ants_class_sub
                              WHERE parentClass = #{parentId}
                      ) ORDER BY  uploadTime DESC LIMIT #{head},#{tail}
        

        
            WHERE goodsClass = #{childId}
                ORDER BY  uploadTime DESC LIMIT #{head},#{tail}

        SELECT
            
                    FROM ants_goods_base
        
            WHERE goodsClass
                   IN (
                      SELECT subClassId
                          FROM ants_class_sub
                              WHERE parentClass = #{parentId}
                      )
                      ORDER BY  goodsPrice DESC LIMIT #{head},#{tail}
        

        
            WHERE goodsClass = #{childId}
                  ORDER BY  goodsPrice DESC LIMIT #{head},#{tail}
